import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setOllamaApiUrl, selectOllamaApiUrl } from '../redux/slices/settingsSlice';
import { CogIcon } from '@heroicons/react/24/outline';

const Settings = () => {
  const dispatch = useDispatch();
  const currentApiUrl = useSelector(selectOllamaApiUrl);
  const [apiUrl, setApiUrl] = useState(currentApiUrl);
  const [saved, setSaved] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(setOllamaApiUrl(apiUrl));
    setSaved(true);
    setTimeout(() => setSaved(false), 2000);
  };

  const handleReset = () => {
    const defaultUrl = 'http://127.0.0.1:11434';
    setApiUrl(defaultUrl);
    dispatch(setOllamaApiUrl(defaultUrl));
    setSaved(true);
    setTimeout(() => setSaved(false), 2000);
  };

  return (
    <div className="p-6">
      <div className="max-w-3xl mx-auto bg-white rounded-lg shadow p-6">
        <div className="flex items-center mb-6">
          <CogIcon className="h-8 w-8 text-blue-600 mr-3" />
          <h2 className="text-2xl font-bold">Settings</h2>
        </div>

        <div className="mb-8">
          <h3 className="text-lg font-medium mb-4">Ollama API Configuration</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="apiUrl" className="block text-sm font-medium text-gray-700">
                API URL
              </label>
              <input
                type="url"
                id="apiUrl"
                value={apiUrl}
                onChange={(e) => setApiUrl(e.target.value)}
                className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 focus:border-blue-500 focus:ring-blue-500"
                placeholder="http://127.0.0.1:11434"
                required
              />
              <p className="mt-2 text-sm text-gray-500">
                The URL where your Ollama server is running. Default is http://127.0.0.1:11434
              </p>
            </div>

            <div className="flex space-x-3">
              <button
                type="submit"
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
              >
                Save Changes
              </button>
              <button
                type="button"
                onClick={handleReset}
                className="px-4 py-2 bg-gray-100 text-gray-700 rounded-md hover:bg-gray-200"
              >
                Reset to Default
              </button>
            </div>
          </form>
        </div>

        {saved && (
          <div className="rounded-md bg-green-50 p-4">
            <div className="flex">
              <div className="ml-3">
                <p className="text-sm font-medium text-green-800">
                  Settings saved successfully
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
