import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  ollamaApiUrl: localStorage.getItem('ollamaApiUrl') || 'http://127.0.0.1:11434',
};

const settingsSlice = createSlice({
  name: 'settings',
  initialState,
  reducers: {
    setOllamaApiUrl: (state, action) => {
      state.ollamaApiUrl = action.payload;
      localStorage.setItem('ollamaApiUrl', action.payload);
    },
  },
});

export const { setOllamaApiUrl } = settingsSlice.actions;
export const selectOllamaApiUrl = (state) => state.settings.ollamaApiUrl;

export default settingsSlice.reducer;
