import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { ollamaAPI, handleApiError } from '../api/api';
import Layout from '../components/Layout';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const { selectedModel } = useSelector((state) => state.models);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages([...messages, userMessage]);
    setInput('');
    setIsLoading(true);
    setError(null);

    try {
      const response = await ollamaAPI.chat(
        selectedModel?.name || 'llama2',
        [...messages, userMessage]
      );
      setMessages([...messages, userMessage, response.data]);
    } catch (error) {
      console.error('Error:', error);
      setError(handleApiError(error).message);
      // Remove the user message if the request failed
      setMessages(messages);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Layout>
      <div className="flex flex-col h-full bg-gray-100">
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {error && (
            <div className="p-4 rounded-lg bg-red-100 text-red-700 max-w-[80%]">
              <p className="text-sm font-semibold mb-1">Error</p>
              <p>{error}</p>
            </div>
          )}
          {messages.map((message, index) => (
            <div
              key={index}
              className={`p-4 rounded-lg ${
                message.role === 'user' ? 'bg-blue-100 ml-auto' : 'bg-white'
              } max-w-[80%]`}
            >
              <p className="text-sm font-semibold mb-1">
                {message.role === 'user' ? 'You' : 'Assistant'}
              </p>
              <p className="text-gray-800">{message.content}</p>
            </div>
          ))}
          {isLoading && (
            <div className="p-4 rounded-lg bg-white max-w-[80%]">
              <p className="text-sm font-semibold mb-1">Assistant</p>
              <p className="text-gray-800">Thinking...</p>
            </div>
          )}
        </div>
        <form onSubmit={handleSubmit} className="p-4 bg-white border-t">
          <div className="flex space-x-4">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="flex-1 p-2 border rounded-lg focus:outline-none focus:border-blue-500"
              disabled={isLoading}
            />
            <button
              type="submit"
              disabled={isLoading}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 focus:outline-none disabled:bg-gray-400"
            >
              Send
            </button>
          </div>
        </form>
      </div>
    </Layout>
  );
};

export default Chat;
